export default (state = {}, action:any) => {
    switch (action.type) {
        case 'LOGIN':
            return { id: action.user.id, username: action.user.username, email:action.email }
        // case 'CREDENTIALS':
        //     return { username: action.username }
        case 'LOGOUT':
            return {}
        default: return state;
    }
}
