import { Box, Button, Typography } from '@mui/material';
import { useState } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

const Home = (props:any) => {
  const [loggedIn, setLoggedIn] = useState(
    props.auth.user !==null ,
  );

  const handleLogout = () => {
    localStorage.removeItem('token');
    setLoggedIn(false);
  };

  return (
    <>
      <Typography variant="h2">Home Page</Typography>
      <Typography>
        You are currently {loggedIn && props.auth.user ? '' : 'NOT'} logged-in
      </Typography>
      {loggedIn && props.auth.user?
      <Typography>
        <Typography>
        Welcome {props.auth.user.username}
        </Typography>
        <Typography>
          Your saved credentials
        </Typography>
        {props.auth.user.degreeCertificate ?
        <Typography>
          <Link to='/'><Typography>Degree Certificate</Typography></Link>
          <Typography><a>Issuer: {props.auth.user.degreeCertificate.issuer}</a></Typography>
          <a href={props.auth.user.degreeCertificate.url}><Typography>Url: {props.auth.user.degreeCertificate.url}</Typography></a>
        </Typography>
        :''}
        <Button onClick={handleLogout}>Logout</Button>
      </Typography>: 
      <Button href="/login">Login</Button>}
    </>
  );
};

const mapStateToProps = (state:any) => ({
  auth:state.auth
})

export default connect(mapStateToProps)(Home);
