import { Button, Typography } from '@mui/material';
import { useState } from 'react';
import { connect } from 'react-redux';

const Home = (props:any) => {
  const [loggedIn, setLoggedIn] = useState(
    localStorage.getItem('token') !== null,
  );

  const handleLogout = () => {
    localStorage.removeItem('token');
    setLoggedIn(false);
  };

  return (
    <>
      <Typography variant="h2">Home Page</Typography>
      <Typography>
        You are currently {loggedIn ? '' : 'NOT'} logged-in
      </Typography>
      {loggedIn?
      <Typography>
        <Typography>
        Welcome {props.auth.user? props.auth.user.username:''}
        </Typography>
        <Typography>
          Your saved credentials
        </Typography>
        {props.auth.user.degreeCertificate?
        <Typography>
          <Typography>Degree Certificate</Typography>
          <Typography>Issuer: {props.auth.user.degreeCertificate.issuer}</Typography>
          <Typography>Url: {}</Typography>
        </Typography>
        :''}
        {/* <Typography>
          Public
        </Typography>
        <Typography>
          Private
        </Typography> */}
        <Button onClick={handleLogout}>Logout</Button>
      </Typography>: 
      <Button href="/login">Login</Button>}
    </>
  );
};

const mapStateToProps = (state:any) => ({
  auth:state.auth
})

export default connect(mapStateToProps)(Home);
